import { useEffect, useState } from "react";
import React from "react";

class Color extends React.Component {
  constructor(props) {
    super(props);
    this.state = { favoriteColor: "red" };
  }
  shouldComponentUpdate(nextProp, nextState) {
    if (this.props.favoriteColor !== nextProp.favoriteColor) {
      return true;
    }
    return false;
  }
  render() {
    return (
      <div>
        <div>My Favorite Color is {this.props.favoriteColor}</div>
        <button
          onClick={this.setState((stste) => ({ favoriteColor: "Blue" }))}
        />
      </div>
    );
  }
}

